changeSet.sql:
CREATE TABLE directory ( id bigint NOT NULL, full_name character varying(2048) NOT NULL UNIQUE );
ALTER TABLE directory ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY ( SEQUENCE NAME directory_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1 );
ALTER TABLE ONLY directory ADD CONSTRAINT pk_directory PRIMARY KEY (id);
CREATE TABLE register ( id bigint NOT NULL, module_id character varying(255) NOT NULL, message_id character varying(512) NOT NULL, header character varying(1024) NOT NULL, directory character varying(2048) NOT NULL, io_scheme character varying(255) NOT NULL, uid character varying(255) NOT NULL UNIQUE, date timestamp with time zone NOT NULL );
ALTER TABLE register ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY ( SEQUENCE NAME register_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1 );
ALTER TABLE ONLY register ADD CONSTRAINT pk_register PRIMARY KEY (id);
CREATE TABLE io_scheme ( id bigint NOT NULL, name character varying(255) UNIQUE NOT NULL, description character varying(1024) NOT NULL, module_id character varying(255) NOT NULL, type character varying(255) NOT NULL, protocol character varying(255) NOT NULL, config jsonb NOT NULL, export_list character varying[] );
ALTER TABLE io_scheme ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY ( SEQUENCE NAME io_scheme_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1 );
ALTER TABLE ONLY io_scheme ADD CONSTRAINT pk_io_scheme PRIMARY KEY (id);
