changeSet.sql:
CREATE TABLE db_message ( id bigint NOT NULL, uuid character varying(255) NOT NULL UNIQUE, trx_id character varying(255) NOT NULL, created timestamp with time zone, updated timestamp with time zone, status character varying(32) NOT NULL, topic character varying(512) NOT NULL, options jsonb NOT NULL, redelivery_counter integer NOT NULL, headers jsonb, content_class character varying(1024), content text, response_class character varying(1024), response text );
ALTER TABLE db_message ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY ( SEQUENCE NAME db_message_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1 );
ALTER TABLE ONLY db_message ADD CONSTRAINT pk_db_message PRIMARY KEY (id);
