CREATE TABLE db_message ( id bigint NOT NULL, uuid character varying(255), trx_id character varying(255), created timestamp with time zone, updated timestamp with time zone, status character varying(10), topic character varying(255), options json, redelivery_counter integer, headers json, content_class character varying(255), content character varying(255), response_class character varying(255), response character varying(255) );
ALTER TABLE db_message ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY ( SEQUENCE NAME db_message_id_seq START WITH 1 INCREMENT BY 1 NO MINVALUE NO MAXVALUE CACHE 1 );
ALTER TABLE ONLY db_message ADD CONSTRAINT pk_db_message PRIMARY KEY (id);
