changeSet.sql:
CREATE TABLE directory (
    id bigint NOT NULL,
    full_name character varying(2048) NOT NULL UNIQUE
);

ALTER TABLE directory ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME directory_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

ALTER TABLE ONLY directory
    ADD CONSTRAINT pk_directory PRIMARY KEY (id);

CREATE TABLE message (
    id bigint NOT NULL,
    uid character varying(255) NOT NULL UNIQUE,
    parent_uid character varying(255),
    created_by character varying(255) NOT NULL,
    created timestamp with time zone NOT NULL,
    updated_by character varying(255),
    updated timestamp with time zone,
    directory_names character varying[] NOT NULL,
    header character varying(1024) NOT NULL,
    content text NOT NULL,
    tags character varying[]
);

ALTER TABLE message ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME message_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

ALTER TABLE ONLY message
    ADD CONSTRAINT pk_message PRIMARY KEY (id);

CREATE TABLE message_directory (
    message_id bigint NOT NULL,
    directory_id bigint NOT NULL
);

ALTER TABLE ONLY message_directory
    ADD CONSTRAINT pk_message_directory PRIMARY KEY (message_id, directory_id);

CREATE INDEX ix_message_directory_directory ON message_directory USING btree (directory_id);

CREATE INDEX ix_message_directory_message ON message_directory USING btree (message_id);

ALTER TABLE ONLY message_directory
    ADD CONSTRAINT fk_message_directory_directory FOREIGN KEY (directory_id) REFERENCES directory(id) ON UPDATE RESTRICT ON DELETE RESTRICT;

ALTER TABLE ONLY message_directory
    ADD CONSTRAINT fk_message_directory_message FOREIGN KEY (message_id) REFERENCES message(id) ON UPDATE RESTRICT ON DELETE RESTRICT;
