changeSet.sql:
CREATE TABLE property_type (
    id bigint NOT NULL,
    type character varying(512) NOT NULL UNIQUE,
    description character varying(1024),
    tag character varying(64) NOT NULL
);

ALTER TABLE property_type ALTER COLUMN id ADD GENERATED BY DEFAULT AS IDENTITY (
    SEQUENCE NAME property_type_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

ALTER TABLE ONLY property_type
    ADD CONSTRAINT pk_property_type PRIMARY KEY (id);
